/**
 * Created by horacio on 3/10/16.
 */

import PIXI from '../lib/pixi';

export default class SpriteGrh extends PIXI.extras.MovieClip {
  constructor(grh, cantLoops) {
    let nullFrames = [];
    nullFrames[0] = {texture: null};
    super(nullFrames);

    cantLoops = cantLoops || 0;

    this._velocidadSeteada = false;
    this._playedLoops = 0;
    this._cantLoops = cantLoops;
    this._realOnComplete = null;

    this.loop = (cantLoops <= 0); // OJO; si loopea por default hace play apenas lo creas

    this.cambiarGrh(grh);

    let self = this;
    this.onComplete = function () {
      if (self._playedLoops < self._cantLoops) {
        self._playedLoops++;
        self.gotoAndStop(0);
        self._play();
      } else {
        self.gotoAndStop(0);
        if (this._realOnComplete) {
          this._realOnComplete();
        }
      }
    };

    this._posicionarGrafico();
  }

  setSize(w, h) {
    this.width = w;
    this.height = h;
    this._posicionarGrafico();
  }

  play() {
    if (this.textures.length > 1) {
      this._playedLoops = 1;
      this._play();
    }
  }

  _play() {
    super.play();
  }

  setOnComplete(cb) {
    this._realOnComplete = cb;
  }

  _setSpeed(velocidad) {
    let duracion;
    if (this._velocidadSeteada) {
      duracion = this._velocidadSeteada;
    } else {
      duracion = velocidad;
    }
    let fps = (this.textures.length / duracion) * 1000;
    this.animationSpeed = fps / 60;
  }

  setSpeed(vel) {
    this._velocidadSeteada = vel;
    this._setSpeed();
  }

  setGridPositionChangeCallback(callback) {
    this._onGridPositionChange = callback;
  }

  setPosition(x, y) {
    this.x = x;
    this.y = y;
    let gridX = Math.round(x / 32);
    let gridY = Math.round(y / 32);
    if ((gridX !== this._gridX) || (gridY !== this._gridY)) {
      this._gridX = gridX;
      this._gridY = gridY;
      if (this._onGridPositionChange) {
        this._onGridPositionChange();
      }
    }
  }

  _posicionarGrafico() {
    let x = ((this.width - 32) / 2) / this.width;
    let y = (this.height - 32) / this.height;
    this.anchor.set(x, y);
  }

  cambiarGrh(grh) {
    if (this._grh === grh) {
      return;
    }
    this._grh = grh;

    if (!grh) {
      this.gotoAndStop(0);
      return;
    }

    let grhAnimacion = !!grh.frames;

    if (grhAnimacion) {
      this.textures = grh.frames;
      this._setSpeed(grh.velocidad);
    } else {
      let aux = [];
      aux.push(grh);
      this.textures = aux;
    }
    if (!this.playing) {
      this.gotoAndStop(0);
    } else {
      this.gotoAndStop(this.currentFrame);
      super.play();
    }
    this._posicionarGrafico();
    if (grhAnimacion && this.loop) {
      this.play();
    }
  }
}
